import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-ventana-ayuda',
  templateUrl: './ventana-ayuda.component.html',
  styleUrls: ['./ventana-ayuda.component.scss']
})
export class VentanaAyudaComponent {
  @Output() seleccionarOpcion = new EventEmitter<string>();
  @Output() cerrar = new EventEmitter<void>();

  elegir(opcion: string) {
    this.seleccionarOpcion.emit(opcion);
  }

  cerrarVentana() {
    this.cerrar.emit();
  }
}
