import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { FormBuilder, ReactiveFormsModule, Validators } from '@angular/forms';
import { UsuarioService } from 'src/app/core/shared/services/usuario.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss'],
   standalone: true,
      imports: [ ReactiveFormsModule, CommonModule],
})
export class RegisterComponent {
 loading = false;
  message = '';
  error = '';

  roles = ['tecnico', 'callcenter', 'administrador'];
  areas = ['fugas', 'desazolve', 'construccion', 'suministro', 'odadren'];

  form = this.fb.group({
    nombre: ['', Validators.required],
    email: ['', [Validators.required, Validators.email]],
    password: ['', Validators.required],
    rol: ['', Validators.required],
    area: ['', Validators.required]
  });

  constructor(
    private fb: FormBuilder,
    private usuarioService: UsuarioService
  ) {}

  submit() {
    if (this.form.invalid) return;

    this.loading = true;
    this.error = '';
    this.message = '';

    this.usuarioService.register(this.form.value).subscribe({
      next: () => {
        this.loading = false;
        this.message = 'Usuario creado correctamente';
      },
      error: (err:any) => {
        this.loading = false;
        this.error = err.error.message || 'Error al registrar usuario';
      }
    });
  }
}
<div class="container">
  <div class="card">
    <h2>Registro de Usuario</h2>

    <form [formGroup]="form" (ngSubmit)="submit()">

      <div class="input-group">
        <label>Nombre</label>
        <input type="text" formControlName="nombre" />
      </div>

      <div class="input-group">
        <label>Email</label>
        <input type="email" formControlName="email" />
      </div>

      <div class="input-group">
        <label>Contraseña</label>
        <input type="password" formControlName="password" />
      </div>

      <div class="input-group">
        <label>Rol</label>
        <select formControlName="rol">
          <option value="">Seleccione...</option>
          <option *ngFor="let r of roles" [value]="r">{{ r }}</option>
        </select>
      </div>

      <div class="input-group">
        <label>Área</label>
        <select formControlName="area">
          <option value="">Seleccione...</option>
          <option *ngFor="let a of areas" [value]="a">{{ a }}</option>
        </select>
      </div>

      <button type="submit" [disabled]="loading">
        {{ loading ? 'Guardando...' : 'Crear cuenta' }}
      </button>

      <p class="success" *ngIf="message">{{ message }}</p>
      <p class="error" *ngIf="error">{{ error }}</p>

    </form>
  </div>
</div>
